// Extract content and token usage from OpenAI response
const results = [];

for (const item of $input.all()) {
  // Try multiple possible locations for the response
  let raw = item.json?.choices?.[0]?.message?.content ||  // Standard OpenAI format
            item.json?.message?.content ||                  // Alternative format
            item.json?.completion ||                        // Legacy format
            item.json?.output ||                            // Generic output
            item.json;                                      // Direct JSON

  // Extract token usage if available
  const usage = item.json?.usage || {
    prompt_tokens: 0,
    completion_tokens: 0,
    total_tokens: 0
  };

  if (!raw) {
    console.log("No content found in item:", JSON.stringify(item.json).substring(0, 200));
    continue;
  }

  let markdown = "";
  let image_prompt = "";

  // If already structured JSON object
  if (typeof raw === "object" && raw !== null && (raw.markdown || raw.image_prompt)) {
    markdown = String(raw.markdown || "").trim();
    image_prompt = String(raw.image_prompt || "").trim();
  } else {
    // Convert to string and clean code fences
    let cleaned = String(raw)
      .trim()
      .replace(/^```json\s*/i, "") // Remove ```json at the start
      .replace(/```$/i, "")        // Remove ``` at the end
      .trim();

    // Try parsing as JSON
    try {
      const parsed = JSON.parse(cleaned);
      markdown = String(parsed.markdown || "").trim();
      image_prompt = String(parsed.image_prompt || "").trim();
    } catch (err) {
      console.log("JSON parse failed, attempting regex extraction");

      // Fallback: regex extraction for malformed JSON
      const markdownMatch = cleaned.match(/"markdown"\s*:\s*"((?:[^"\\]|\\.)*)"/s);
      const imageMatch = cleaned.match(/"image_prompt"\s*:\s*"((?:[^"\\]|\\.)*)"/s);

      if (markdownMatch) {
        markdown = markdownMatch[1]
          .replace(/\\"/g, '"')
          .replace(/\\n/g, "\n")
          .replace(/\\\\/g, "\\")
          .trim();
      } else {
        // If no JSON structure found, use entire cleaned content as markdown
        markdown = cleaned;
      }

      if (imageMatch) {
        image_prompt = imageMatch[1]
          .replace(/\\"/g, '"')
          .trim();
      }
    }
  }

  // Final cleanup for markdown
  markdown = markdown
    .replace(/^```/i, "")
    .replace(/```$/i, "")
    .trim();

  // Calculate costs (GPT-4o-mini pricing)
  const input_cost = usage.prompt_tokens * 0.00000015;   // $0.150 per 1M tokens
  const output_cost = usage.completion_tokens * 0.0000006; // $0.600 per 1M tokens
  const total_cost = input_cost + output_cost;

  results.push({
    json: {
      markdown: markdown,
      image_prompt: image_prompt,
      // Token usage data
      prompt_tokens: usage.prompt_tokens,
      completion_tokens: usage.completion_tokens,
      total_tokens: usage.total_tokens,
      input_cost: input_cost,
      output_cost: output_cost,
      total_cost: total_cost,
      model: item.json?.model || "gpt-4o-mini"
    },
  });
}

if (results.length === 0) {
  throw new Error("No valid content extracted from OpenAI response. Check input structure.");
}

return results;

