  if (typeof message === 'string') {
    // Remove starting `````` or ```
    message = message.replace(/^```[\s\S]*?\n/, '');
    // Remove ending ```
    message = message.replace(/```$/, '').trim();
  }

  let llmOutput;
  try {
    llmOutput = JSON.parse(message);
  } catch (err) {
    llmOutput = { raw: item.json.message, parseError: err.message };
  }

  results.push({
    json: {
      validation_results: JSON.stringify(llmOutput, null, 2),  // Stringify for LLM readability
      validation_results_raw: llmOutput,  // Keep parsed object for other nodes
      id: item.json.id,
      citations: item.json.citations
    }
  });
}

return results;

