const results = [];

for (const item of $input.all()) {
  // Access the nested content from the message object
  let raw =
    item.json?.message?.content ||
    item.json?.completion ||
    item.json?.output ||
    item.json?.choices?.[0]?.message?.content ||
    item.json;

  if (!raw) continue;

  // If already structured JSON (object form)
  if (typeof raw === "object" && raw !== null && (raw.markdown || raw.image_prompt)) {
    results.push({
      json: {
        markdown: String(raw.markdown || "").trim(),
        image_prompt: String(raw.image_prompt || "").trim(),
      },
    });
    continue;
  }

  // Convert to string and clean out code fences or JSON labels
  let cleaned = String(raw)
    .trim()
    .replace(/^```(?:json)?/i, "") // remove starting ``` or ```json
    .replace(/```$/i, "") // remove ending ```
    .trim();

  let parsed;
  try {
    // Try parsing as JSON
    parsed = JSON.parse(cleaned);
  } catch (err) {
    // Fallback for malformed JSON or text
    const markdownMatch = cleaned.match(/"markdown"\s*:\s*"((?:[^"\\]|\\.)*)"/s);
    const imageMatch = cleaned.match(/"image_prompt"\s*:\s*"((?:[^"\\]|\\.)*)"/s);

    parsed = {
      markdown: markdownMatch
        ? markdownMatch[1].replace(/\\"/g, '"').replace(/\\n/g, "\n").trim()
        : cleaned.trim(),
      image_prompt: imageMatch
        ? imageMatch[1].replace(/\\"/g, '"').trim()
        : "",
    };
  }

  // Final cleanup for markdown text
  const markdown = String(parsed.markdown || "")
    .replace(/^```(?:markdown)?/i, "")
    .replace(/```$/i, "")
    .trim();

  results.push({
    json: {
      markdown,
      image_prompt: String(parsed.image_prompt || "").trim(),
    },
  });
}

return results;
