// Initialize an array for results
const results = [];

function safeParse(content) {
  let c = content.trim();

  // Strip code fences like ```json ... ```
  c = c.replace(/^```[a-zA-Z]*\s*/, "").replace(/```$/, "").trim();

  // If it starts with "json\n" or "json ", remove that label
  if (c.toLowerCase().startsWith("json")) {
    c = c.replace(/^json\s*/i, "");
  }

  let parsed;
  try {
    parsed = JSON.parse(c);
  } catch (err) {
    // Try to fix broken JSON by escaping unescaped quotes inside values
    let fixed = c
      // replace line breaks inside strings with space
      .replace(/\n/g, " ")
      // escape unescaped double quotes inside values
      .replace(/:\s*"([^"]*?)"([^,\}\]])/g, (m, g1, g2) => {
        return `: "${g1.replace(/"/g, '\\"')}"${g2}`;
      });

    try {
      parsed = JSON.parse(fixed);
    } catch (err2) {
      // Sometimes the LLM double-stringifies JSON -> try parsing twice
      try {
        parsed = JSON.parse(JSON.parse(c));
      } catch (err3) {
        // Fallback: force-wrap into valid object
        parsed = { markdown: c, image_prompt: "" };
      }
    }
  }

  // Normalize parsed output: ensure both keys exist
  return {
    markdown: (parsed.markdown || "").toString().trim(),
    image_prompt: (parsed.image_prompt || "").toString().trim()
  };
}

for (const item of $input.all()) {
  let rawContent = item.json.completion || item.json.output || item.json?.choices?.[0]?.message?.content;
  if (!rawContent) continue;

  const parsed = safeParse(rawContent);

  // Clean markdown in case it still has code fences
  const cleanedMarkdown = parsed.markdown
    .replace(/^```(markdown)?/gm, "")
    .replace(/```$/gm, "")
    .trim();

  results.push({
    json: {
      markdown: cleanedMarkdown,
      image_prompt: parsed.image_prompt
    }
  });
}

return results;
